import mongoose from "mongoose";
import initDb from "../batches/tasks/initDatabase";
import addTenant from "../batches/tasks/addTenant";

import { MongoMemoryServer } from 'mongodb-memory-server';

import moment from "moment";
import * as _ from "lodash";
//import defaults from "superagentdefaults";
//import * as test_helper from "../test/helper";
import { Swift } from "../storages/Swift";

import * as controller from "../controllers/files";
import User from "../models/User";
import Tenant from "../models/Tenant";


jest.setTimeout(40000);
const tenant_name = 'test'

const sample_file = {
  "name": "file.pdf",
  "mime_type": "application/pdf",
  "size": 3028,
  "base64": "data:application/pdf;base64,",
  "checksum": "2bdf57f7ab102b8fcaa80979f24abf76",
  "authorities": [],
  "tags": [],
  "meta_infos": []
}

describe('lib/controllers/files', () => {
  let default_res
  let mongoServer;
  const opts = { useNewUrlParser: true }; // remove this option if you use mongoose 5 and above  
  let tenant
  let user
  beforeAll(async () => {
    mongoServer = new MongoMemoryServer();
    const mongoUri = await mongoServer.getConnectionString();
    await mongoose.connect(mongoUri, opts, (err) => {
      if (err) { console.error(err); }
    });
    await initDb()
    await addTenant(tenant_name)

    tenant = (await Tenant.findOne({ name: tenant_name })).toObject()
    user = (await User.findOne({ account_name: `${tenant_name}1` })).toObject()
    default_res = {
      user: { ...user, tenant_id: tenant._id, tenant: { ...tenant } }
    }
  })
  afterAll(async () => {
    await mongoose.disconnect();
    await mongoServer.stop();
    console.log("....terminated")
  })

  describe(`upload()`, () => {
    it(`テナント情報の取得`, async () => {
      expect(tenant_name).toBe(tenant.name)
    })

    it(`パラメータ不正: files is empty`, async () => {
      const req = {
        body: { files: [] }
      }
      const res_json = jest.fn()
      const res = { user: { ...default_res.user }, status: jest.fn(() => ({ json: res_json })) }
      await controller.upload(req, res)

      expect(res.status.mock.calls[0][0]).toBe(400) // http response statusは400
      const res_body = res_json.mock.calls[0][0] //1回目の第一引数
      expect(res_body.status.success).toBe(false)
      expect(res_body.status.message).toBe("ファイルのアップロードに失敗しました")
      expect(res_body.status.errors.files).toBeTruthy() //エラーが存在する
    });
    it(`1ファイル成功`, async () => {
      const req = {
        body: { files: [{ ...sample_file }], dir_id: tenant.home_dir_id }
      }
      const res_error_json = jest.fn()
      const res = { user: { ...default_res.user }, json: jest.fn(), status: jest.fn(() => ({ json: res_error_json })) }
      await controller.upload(req, res)

      if (res.json.mock.calls.length === 0) {
        expect(res_error_json.mock.calls[0][0].status.errors).toBe('テストを遂行できませんでした')
      } else {
        const res_body = res.json.mock.calls[0][0] //1回目の第一引数
        expect(res_body.status.success).toBe(true)
        expect(res_body.body.length).toBe(1) //１ファイルの結果が返る
      }
    });
    it(`3ファイル成功`, async () => {
      const req = {
        body: { files: [{ ...sample_file }, { ...sample_file }, { ...sample_file }], dir_id: tenant.home_dir_id }
      }
      const res_error_json = jest.fn()
      const res = { user: { ...default_res.user }, json: jest.fn(), status: jest.fn(() => ({ json: res_error_json })) }
      await controller.upload(req, res)

      if (res.json.mock.calls.length === 0) {
        expect(res_error_json.mock.calls[0][0].status.errors).toBe('テストを遂行できませんでした')
      } else {
        const res_body = res.json.mock.calls[0][0] //1回目の第一引数
        expect(res_body.status.success).toBe(true)
        expect(res_body.body.length).toBe(3) //3ファイルの結果が返る
      }

    });
  })
  describe(`rename()`, () => {

  })

});

